INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP001', '巴西', '造纸', '行业需要利用其他可再生的原材料来代替木材，减少对森林资源的依赖，并且降低生产成本', '源头控制', '资源高效利用', '原料多样化技术', '通过采用多样化的原料，如竹子、稻草、秸秆等非木材原料，降低木材消耗和环境影响', '王英斌.巴西制浆造纸行业力推ESG管理[J].纸和造纸,2023,42(01):68.', 1);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP002', '巴西', '造纸', '巴西造纸行业需要采用生物质发电、生物燃料和生物质热能利用等技术，将生产废弃物和废水等资源转化为能源，降低能源消耗和碳排放', '生产过程', '资源高效利用', '生物质能源技术', '该技术包括生物质发电、生物燃料和生物质热能利用等，旨在降低造纸行业的能源消耗和碳排放', 'BRIC Countries (Brazil, Russia, India, China) Paper and Paperboard Market Summary, Competitive Analysis and Forecast, 2017-2026
https://www.researchandmarkets.com/reports/5665478/bric-countries-brazil-russia-india-china#tag-pos-1', 2);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP003', '巴西', '造纸', '需要更加高效的生产工艺和设备来减少能源消耗', '生产过程', '资源高效利用；资源化控制', '能源节约和利用技术', '采用高效节能设备和系统，优化能源利用效率，同时回收废热和废液，降低生产成本和环境影响', 'Paper and Paperboard Retail BRIC (Brazil, Russia, India, China) Industry Guide - Market Summary, Competitive Analysis and Forecast to 2025
https://www.researchandmarkets.com/reports/5463674/paper-and-paperboard-retail-bric-brazil#tag-pos-2', 3);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP004', '巴西', '造纸', '采用可再生能源，例如风能、太阳能、生物质和水力发电等。这些能源可以替代传统的化石燃料，减少碳排放，提高能源效率', '生产过程', '资源高效利用', '可再生能源利用技术', '采用可再生能源，例如风能、太阳能、生物质和水力发电等。这些能源可以替代传统的化石燃料，减少碳排放，提高能源效率', '巴西纸浆造纸行业的技术演变https://online.fliphtml5.com/rhwu/zbrk/#p=1', 4);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP005', '巴西', '造纸', '生产过程清洁化技术是巴西造纸行业当前重要的技术需求，旨在提高生产效率和产品质量的同时减少环境污染和废弃物产生。', '生产过程', '能源节约及清洁化', '生产过程清洁化技术', '采用清洁生产技术，优化原料选择和加工过程，减少污染物排放和废弃物产生', '巴西纸浆造纸行业的技术演变https://online.fliphtml5.com/rhwu/zbrk/#p=1', 5);
