INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP007', '巴西', '造纸', '包括采用低氮氧化物燃烧技术、气体燃烧余热回收技术、燃料多样化等措施，降低工业燃料的污染排放', '末端控制', '大气污染防治', '环保清洁燃料技术', '包括采用低氮氧化物燃烧技术、气体燃烧余热回收技术、燃料多样化等措施，降低工业燃料的污染排放', '2022年报https://iba.org/datafiles/publicacoes/relatorios/relatorio-anual-iba2022-compactado.pdf', 7);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP008', '巴西', '造纸', '采用烟气脱硫技术，如湿法脱硫、半干法脱硫等，减少二氧化硫等有害气体的排放。', '末端控制', '大气污染防治', '脱硫技术', '采用烟气脱硫技术，如湿法脱硫、半干法脱硫等，减少二氧化硫等有害气体的排放。', '2022年报https://iba.org/datafiles/publicacoes/relatorios/relatorio-anual-iba2022-compactado.pdf', 8);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('PP009', '巴西', '造纸', '该技术包括植树、育苗、森林保护和再生等，旨在保护环境和森林资源，确保可持续发展', '源头控制', '资源化控制', '可持续森林管理技术', '该技术包括植树、育苗、森林保护和再生等，旨在保护环境和森林资源，确保可持续发展', '2022年报https://iba.org/datafiles/publicacoes/relatorios/relatorio-anual-iba2022-compactado.pdf', 9);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('TE001', '巴西', '纺织', '巴西纺织工业联合会（ABIT）指出，纺织业是一个高度污染的行业，因此需要采用更环保的染色技术。', '生产过程', '能源节约及清洁化', '环保染色技术', '采用氧化还原染色剂、离子液体染色剂等环保染色技术，可以降低水资源的使用和化学污染物的排放。', '巴西纺织服装行业协会https://www.abit.org.br/noticias', 10);
INSERT INTO demand.demand_detail (number, region, industry, description, stage, tech_categories, tech, tech_overview, source, demand_id) VALUES ('TE002', '巴西', '纺织', '巴西政府的能源和矿产资源部（MME）指出，纺织业是一个能源消耗大户，因此需要采用更节能的生产技术。', '生产过程', '能源节约及清洁化', '节能技术', '采用高效节能的纺纱机、织机等设备，可以降低能源消耗和碳排放。', '巴西纺织品产业发展迅速，成为全球重要的纺织品制造中心https://www.wyapsw.com/info/d3956164g.html', 11);
