create table detail
(
    detail_id     int auto_increment
        primary key,
    industry      varchar(50)  null,
    region        varchar(50)  null,
    standard      varchar(200) null,
    number        varchar(200) null,
    effectiveness varchar(20)  null,
    abstracts     varchar(600) null,
    link          varchar(200) null,
    scope         int          null,
    year          int          null
);

