INSERT INTO standard.detail (detail_id, industry, region, standard, number, effectiveness, abstracts, link, scope, year) VALUES (21, '造纸', '巴西', '第14.128号文件规定了国家有关材料回收利用的政策和适用于固体废物管理的经济和财政手段', null, '是', '法令由 7 条组成，规定了国家材料回收政策以及适用于固体废物管理的经济和金融工具。', 'http://www.almg.gov.br/ ', 3, 2001);
