INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE001', '中国', '水泥', '水泥窑尾烟气高温电除尘+选择性催化还原脱硝技术', '生产过程', '	
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE002', '中国', '水泥', '水泥窑尾烟气高温电除尘+金属纤维毡过滤除尘 +选择性催化还原脱硝技术', '生产过程', '水泥窑尾烟气依次通过高温电除尘、金属纤维毡过滤除尘和SCR脱硝反应器，完成高效除尘和脱硝，脱硝产物为N2和H2O，无二次污染。出口 NOx 浓度< 50 mg/m3，氨逃逸< 2.5 mg/m3；系统总阻力< 1500 Pa。', '进入SCR脱硝反应器的含尘浓度显著降低。脱硝效率高，催化剂寿命长。', '大气污染防治', '碳减排', '登电集团水泥有限公司', '1068万元', null, null, '2021 年《国家先进污染防治技术目录（大气污染防治、噪声与振动控制领域）》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE003', '中国', '水泥', '工业烟气气动脱硫除尘超低排放技术', '生产过程', '通过气动脱硫单元使原烟气剧烈旋转，与脱硫浆液形成两相旋流并互相扰动，从而实现脱硫。脱硫烟气通过气动高效除雾器脱除烟气中的雾滴和微小粉尘，使得烟气污染物含量达到超低排放标准。适用于	脱硫出口二氧化硫浓度小于 35 mg/m3（标态，干基，6%O2）、烟尘浓度小于 5 mg/m3（标态，干基，6%O2）。', '脱硫除尘', '大气污染防治', '烟气脱硫/除尘', null, null, '本技术已经进入工程规模应用阶段，工艺路线成熟、设备及系统集成完善。有近40项工程应用实例，实现技术产值6.38亿元。 ', '（1）工艺创新：创新烟气湿法脱硫除尘超净排放工艺，并进行了工艺流程和系统优化； （2）设计创新：优化设计气动脱硫塔、气动脱硫单元等核心设备、部件，对新型高效除雾器进行原创研究和设计创新，提高了系统脱硫除尘效率和综合性能。', '天津市节能低碳与环境污染防治技术指导目录（第三辑）', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE004', '中国', '水泥', '水泥窑炉烟气脱硫脱硝一体化技术', '生产过程', '	
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE005', '中国', '水泥', '预热器低温脱硫技术', '生产过程', '在原脱硝系统的基础上，利用脱硝原有的氨水罐、出口管道及电控元件，只需增加一套输送和喷射装置，投入少，见效快，安装方便，操作简单，具有较强的实用性', '该项革新有效的控制了停磨期间SO2的升高，避免了废气的超标排放，环境效应得到保障；停磨后回转窑不需要再减料调整，回转窑的台时得到稳定，减少了因窑减产带来的经济损失，每次因停磨减产造成熟料损失约三万多元。', '大气污染防治', '烟气脱硫', '枣庄市沃丰水泥有限公司', '1.2万元', null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE006', '中国', '水泥', '高效脱硝低氧还原燃烧法', '生产过程', '公司自主创立了“WMF高效脱硝低氧还原燃烧法”，对氮氧化物采用低氮分级燃烧脱硝技术及多层喷氨技术，准确控制和优化窑炉煤量比，从源头上控制氮氧化物的生成量，并采用分解炉分煤燃烧控制技术，通过不断的研究分析实现了氮氧化物控制NOx排放<100mg/Nm3。', '在于空气和煤粉分级的位置、程度及氨水喷枪的形式、喷入位置、角度等，其脱硝效率高低不等。', '大气污染防治', '烟气脱硝', '枣庄市沃丰水泥有限公司', '4.91万元', null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE007', '中国', '水泥', '高固气比水泥悬浮预热分解技术', '生产过程', '高固气比预热分解系统主要由高固气比预热器和外循环式分解炉构成，其中高固气比预热器由两列并行排列的旋风预热单元组成。通过提高系统内固体物料与气流的质量比，达到提高系统热效率、增强系统热稳定性的效果。主要技术指标：①窑尾废气温度小于260℃；②水泥熟料烧成的可比热耗低于每公斤熟料2675kJ（640kcal）；③吨熟料电耗小于56kWh；④窑系统运转率大于90%', '与同规格回转窑的新型干法水泥生产线相比，产量增加40%以上，热耗减少20%以上，单位电耗减少15%以上，废气中的SO2排放降低70%以上，NOx 排放降低50%以上。', '能源节约及清洁化', '物料预热降耗', '尧柏阳山庄水泥有限公司', null, '2011年5月该技术已通过陕西省科学技术厅组织的技术鉴定。该技术2011年被评为“中国高等学校十大科技进展”，入选国家工信部《工业节能十二五规划》，荣获“第四届节能中国十大应用技术”', null, '国家重点节能技术推广目录（第四批）', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE008', '中国', '水泥', '利用水泥窑协同处置工业废水技术', '源头控制', '	
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE009', '中国', '水泥', '利用水泥窑协同处置固体废物技术', '源头控制', '	
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE010', '中国', '水泥', '建筑垃圾再生产品制备混凝土技术', '源头控制', '将废弃的建筑垃圾进行一级破碎、二级破碎、一级筛分、二级筛分等工序后，作为骨料替代部分砂石或作为微粉替代部分水泥，可减少水泥或砂石的使用量，降低碳排放。项目用地200 亩、交通运输距离 30公里范围之内较宜。', '建设规模为150 万吨/年处置利用建筑垃圾，碳减排量可达119000吨CO2/年。', '固体废弃物处置及资源化', '原料替代', '北京联绿技术有限责任公司、北京新奥混凝土集团有限公司', '56000万元', null, null, '国家重点节能低碳技术推广目录（2017年本 低碳部分）', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE011', '中国', '水泥', '建筑垃圾中微细粉再生利用技术', '源头控制', '以建筑垃圾为原料，利用固体物料在机械力作用下发生晶格畸变、表面断键等特征，使粉体表面具有较高的表面能；利用碱性化学激发剂对处于介稳状态的玻璃体起到解离和促进水化作用，在化学激发剂形成的化学力与机械力协同作用下，显著提高再生胶凝材料的水化活性和粉磨效率，以此制得高活性矿物掺合料，可以替代部分水泥，从而实现碳减排。', '适用于城市建筑垃圾再生资源化利用，年处理 200万t建筑垃圾，100 万t 工业废渣，碳减排量可达300000吨CO2/年', '固体废弃物处置及资源化', '原料替代', '华新水泥股份有限责任公司、华润水泥（广西）有限公司、重庆垚神建筑垃圾回收处置有限公司', '4000万元', '·', null, '国家重点节能低碳技术推广目录（2017年本 低碳部分）', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE012', '中国', '水泥', '全制砂配制混凝土技术的研究', '源头控制', '技术革新，全部使用机制砂配制混凝土', '自产机制砂价格24.5元/吨，外购河沙价格84.5元/吨，差价60元/吨，则每方混凝土降低15元。另外混凝土强度及耐久性能会得以大幅度改善，消除因使用劣质河沙带来的混凝土工程质量隐患，为商混企业创立全部使用机制砂配料典范，防止河沙资源过度开采，保护生态环境。', '能源节约及清洁化', '原料替代', '青州中联水泥有限公司', null, null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE013', '中国', '水泥', '水泥粉磨系统创新增效技术改造', '生产过程', '公司通过水泥磨隔仓板改造、入V选四通溜子改造、水泥磨空压机改造；水泥粉磨系统新增在线粒度仪、水泥系统普通三相异步电机改为永磁同步电机、水泥系统气力与机械复合混料机改造、水泥粉磨系统皮带机及提升机液耦改磁耦、水泥储存库均化提质改造等方式对公司水泥粉磨系统进行改造。', '1、水泥磨隔仓板改造，水泥产能按照年产70万吨计算，平均电价按照0.55元/kwh计算，年可增加收益为77000元
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE014', '中国', '水泥', '水泥回转窑系统降阻增效改造 ', '生产过程', '改造内容：
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE015', '中国', '水泥', '翻板阀改造', '生产过程', '将企业窑尾预热器系统采用的锁风效果较差，系统内漏风严重，容易造成煤耗电耗偏高的老式传统单翻板锁风阀进行改造；并对预热器系统进行优化，降低生产煤耗电耗，对预热器C1-C2 单翻板锁风阀（共计10个）进行改造。', '改造后预期标煤耗可降低0.5kg／t，综合电耗降0.5kwh／t，C1出口温度较前期降低约10℃，年综合效益约159.3万元。', '大气污染防治', '设备改造', '滕州中联水泥有限公司', null, null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE016', '中国', '水泥', '烧成系统新型隔热节能耐火砖运用更换', '生产过程', '通过将烧成系统新型隔热节能耐火砖运用更换，采用新型隔热节能耐火砖替代原有的普通耐火砖，新型隔热耐火砖应用于整个回转窑内衬筑砌', '技改后回转窑筒体整体平均温度低于200℃，较当前筒体平均温度降低约120℃，可实现标煤耗降低降低4-5kg／t；按年产熟料110万吨，年可节约成本约467.5万元。', '能源节约及清洁化', '设备改造/碳减排', '滕州中联水泥有限公司', null, null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE017', '中国', '水泥', '节能风机改造', '生产过程', '改造为高效节能风机', '经节能改造后，产生的直接经济效益如下：每年将减少285.67万kWh耗电量；每年将节约171.40万元成本。同时，还将间接产生以下效益（按每万度电折合3吨标煤，排放8.72吨C02算）：每年可节省标煤约857.01吨；每年可减少C02排放约2491.04吨。', '能源节约及清洁化', '设备改造/碳减排', '滕州中联水泥有限公司', null, null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE018', '中国', '水泥', '袋收尘超低排放改造', '生产过程', '企业窑尾烟气处理采用的低压长布袋除尘器，原设计的粉尘浓度排放浓度＜20mg／Nm3的要求为依据，现在已不能满足当前国家和地方的环保要求。要实现超低排放，排放目标在 5mg／Nm3左右。项目利用原有除尘器本体，不需改造除尘器本体加高，只对布袋及袋笼增加长度即可，同时对除尘器的防腐、密封一并完善，达到节省投资，实现超低排放要求。', '通过上述技改后，工况正常运行时，布袋过滤风速在0.55-0.60m／min，达到了2mg／Nm3以下，实现了超低排放要求。', '大气污染防治', '设备改造/碳减排', '滕州中联水泥有限公司', null, null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE019', '中国', '水泥', '布袋除尘器', '生产过程', '尘粒在绕过滤布纤维时，受到惯性力作用与纤维碰撞而拦截。由于纤维间得空隙小于气体分子冲击不断改变运动方向，尘粒便与纤维碰撞而被分离出来。其工作过程与滤料的编制方法。纤维的密度及粉尘的扩散．惯性．遮挡．滤布是布袋除尘器的关键，性能良好的滤布，除特定的致密度和透气性外，还应有良好的性。耐热性及较高的机械强度。耐热性良好的纤维，其耐热度目前可达250℃－350℃。', '除尘效率可达99.9%', '大气污染防治', '设备改造', '滕州中联水泥有限公司', null, null, null, 'http://www.zhixinhb.cn/bdccq/2978.html', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('CE020', '中国', '水泥', '水泥工业大脑', '生产过程', '该项目将大数据、人工智能等技术与水泥熟料生产工艺深度融合，围绕水泥熟料生产的能耗优化、质量稳定，利用人工智能技术精准建模，寻求产量、质量、能耗之间的最佳控制参数，在不改变生产工艺和生产设备的前提下，达到高产量、低能耗、质量稳定的目标。项目研发主要分为数据处理、算法建模、机器学习、自动驾驶四个步骤。', '水泥工业大脑上线运行后，综合能耗下降6.73％。2020年实现同比节约标准煤1.54万吨，节电242.3万度，减排二氧化碳7187.5吨、氮氧化合物166.6吨、二氧化硫179.5吨。', '资源高效利用/能源节约及清洁化', '智能化/优化控制/深度学习', '山东东华水泥有限公司', null, null, null, '《水泥行业清洁生产调研报告》', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE001', '中国', '石化', '隔油', '源头控制；末端处理', '隔油是重力分离方法的一种，浮油、粗分散油和悬浮物利用两相的重力差进行分离，分离设施有平流、多层平板、斜板和斜管隔油池等。油珠在废水中的上浮速率是隔油池设计的主要依据，一般认为油珠上浮速率主要取决于粒径的大小及油品的密度和黏度。多层平板和斜板、斜管隔油池的分离规律是：沉降澄清液量和沉降槽的总表面积成正比，沉降所需时间和沉降垂直距离成正比因此，它们的分离效率大大优于一般平流隔油池。', '通过物理或化学手段将两种液体分离，以实现回收和利用油品的目的。当油珠粒径越大、油品和水的密度差越大、污水黏度越小时，隔油效果越好。', '水污染防治；资源高效利用', '污染防治；水油分离', '中国石化化工总公司/中国石油化工股份有限公司/中国海洋石油总公司', null, null, null, null, '隔油技术在石化行业中通常被用于源头控制，也就是在原料加工、生产工艺设计等阶段使用，旨在避免油污物进入水体、土壤等自然环境中。这种技术可以减少污染物的排放，降低环境风险和治理成本，并且有助于提高生产效率和节约资源。在一些特定情况下，隔油技术也可以用于末端处理，但这通常是在其他治理手段无法完全清除油污物时使用的辅助手段。');
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE002', '中国', '石化', '加压溶气气浮法', '末端处理', '溶气气浮分离，是将油珠或颗粒物强制吸附在微细空气泡表面上，使其形成比水轻的颗粒体浮上水面和水分离。油珠等黏附在空气泡表面的几率，首先取决于油珠和空气泡之间的 碰撞速度和碰撞率。另外，其黏附情况和气泡的气相、油珠的液相及水的三相间的界面张力，即和其相互之间的接触角有密切关系。溶气气浮分离法的种类很多，其中最常用的一种是加压溶气气浮法。其过程是用泵将废水送入溶气罐，加压到0.3-0.55 Mpa，同时注入空气，使空气在压力下溶解和分散于废水，一般溶气时间在1~3 min，然后废水通过释放器进入气浮池，由于骤然减压，便形成许多微细气泡逸出，从而捕集水中分散油和悬浮物并实现上浮分离。主要优点是气泡直径小，一般为30~120 mm。因此，在供气量相同的情况下，气泡的总表面积大，吸附能力大；同时气泡上浮的速率慢，与被吸附杂质的接触时间长，从而提高气浮效果', '高效净化水质：加压溶气气浮法能够高效地去除水中的悬浮物、油脂、蛋白质、颜料、氨氮等有机污染物和无机污染物，能够显著提高水质的净化效果。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE003', '中国', '石化', '涡凹气浮法', '末端处理', '经过预处理后的污水流入装有涡凹曝气机的小型充气段，污水在上升过程中通过充气段与曝气机产生的微气泡充分混合，曝气机将水面上的空气通过抽风管道转移到水下。曝气机的工作原理是利用空气输送管底部散气叶轮的高速转动在水中形成一个真空区，液面上的空气通过曝气机输入水中，填补真空，微气泡随之产生并螺旋形地上升到水面，空气中的氧气也随之溶入水中。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE004', '中国', '石化', '烟气湿法洗涤', '末端处理', '烟气湿法洗涤（FGS工艺）是烟气与吸附剂反应脱除其中的SO2与颗粒物，吸收剂为石灰石、钠碱、海水等，脱硫技术为典型的湿法洗涤工艺，主要包括湿式气体洗涤器（WGS）和净化处理单元（PTU）两部分，在WGS中使用苛性碱或苏打吸收烟气中的SO2并转化为硫酸钠，夹带的粉尘通过缓冲溶液进行洗涤。洗涤液沉淀后进入PTU单元，再经酸碱中和、沉降、氧化、提浓、脱水后变成符合卫生要求的固体物进行填埋处理。以钠碱为吸收剂时SO2和粉尘脱除率均可达90%以上，固体废弃物少。缺点是试剂费用高。', '气体净化：该技术可以有效地去除石化生产过程中排放的废气中的污染物，如硫化物、氮氧化物、氨气、苯、苯乙烯等有机物等。烟气湿法洗涤技术通过将废气与水接触，将污染物吸附在水中，从而实现气体净化的目的。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE005', '中国', '石化', '湿法制硫酸', '末端处理', '湿法制硫酸通过固相转化器将烟气中的SO2氧化为SO3，SO3再经水吸收生成硫酸。该工艺技术脱硫率可达95%，且不消耗化学试剂和吸收剂，不产生废弃物，过程简单，操作维护费用较低。', '环保净化：湿法制硫酸技术可以有效地去除石化工业废气中的二氧化硫（SO2），从而实现废气的净化和环保的目的。在该技术中，SO2通过与水反应生成亚硫酸和硫酸，从而实现二氧化硫的净化。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE006', '中国', '石化', '干法/半干法烟气脱硫', '末端处理', '干法工艺使用干粉作吸收剂，半干法工艺则使用湿吸收剂但做成干粉使用。吸收剂在稀相提升管流化床吸附器（ESR）中吸收烟气中的SO2吸收饱和的吸收剂在再生塔鼓泡床中经燃料气再生，再回到吸收器中。吸附剂为金属氧化物（如MgO）,操作温度在120 ~930摄氏度，脱硫率达到95%以上。该工艺的优点是 不降低排气温度，扩散效果好，没有水污染问题；不足之处是吸附反应仅在固体表面进行，内部反应时间长，吸附塔很大且吸收剂用量大。', '环保净化：干法/半干法烟气脱硫技术可以有效地去除石化工业废气中的二氧化硫（SO2），从而实现废气的净化和环保的目的。在该技术中，通过加入适量的氧化剂和碱性吸收剂，将SO2氧化为硫酸根离子，从而实现二氧化硫的净化。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE007', '中国', '石化', '低磷水处理技术', '源头控制', '缓蚀剂的作用是其在金属表面与腐蚀介质两相之问形成一层保护膜，通常可分为三种类型：氧化膜、沉淀膜和吸附膜。形成氧化膜的缓蚀剂主要为无机盐氧化剂，如铬酸盐、重铬酸盐、钼酸盐、钨酸盐、亚硝酸盐等；形成沉积膜的缓蚀剂主要为有机盐，如羧酸盐、有机磷酸、聚磷酸盐等，它们与水中的Ca2+等两价金属离子或作为缓蚀剂加人的Zn2+作用，在金属表面形成难溶性沉积膜；形成吸附膜的缓蚀剂主要为某些含O、N、S等杂原子、具有表面活性的有机物，如有机胺类、硫脲类、咪唑啉类、芳香唑类等，它们大都具有能吸附于金属表面的亲水基团和遮蔽金属表面的疏水基团，在清洁的金属表面形成覆盖膜，从而抑制腐蚀。对低磷水处理技术，由于磷酸盐派度降低，一定程度影响其缓蚀效果，因而必须与其他类型的缓蚀剂如锌盐进行复合，利用两者之间存在的协同增效作用原理，弥补了因磷酸盐㳖庋降低而影响循环水处理的缓蚀效果。', '低磷水处理技术可以有效地降低水中磷酸盐的含量，减少对水体的污染，提高废水的水质，达到一定的排放标准，降低环境污染的程度。低磷水处理技术使用的药剂成本较低，且处理后的水质较高，可以降低后续处理的成本，节约企业的运营成本。', '水污染防治', '污染防治；节约资源；循环水处理', '中国石油天然气集团公司/中国石油化工股份有限公司/中海油能源发展股份有限公司', null, null, null, null, null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE008', '中国', '石化', '无磷水处理技术', '源头控制', '在循环水处理技术领域，由于当前尚无特效的无磷缓蚀剂，因此，无磷水处理技术解决循环水系统设备腐蚀主要是利用无机锌盐与其他类型的缓蚀剂之间存在的协同增效作用，提高其缓蚀剂效果，解决设备腐蚀。并利用具有优异阻垢分散效果的多元共聚物，解决设备的结垢。使用低毒高效的杀生剂，控制滋生生物黏泥，从而使循环水的处理效果满足生产装置长周期运行的需要。', '降低废水中磷的含量：无磷水处理技术可以通过化学沉淀、生物吸附等方法将废水中的磷去除，从而降低废水中磷的含量。这有助于减少水体富营养化的程度，避免水体产生蓝藻等有害生物。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE009', '中国', '石化', '剩余污泥减量生化技术', '生产过程', '当前开发的剩余污泥减量的生化工艺技术类型较多，如好氧一沉淀一厌氧(OSA）工艺、高浓度溶解氧工艺、与臭氧结合的活性污泥工艺等，其中活性污泥与分离膜结合形成的膜生物反应器(MBR） 技术最具代表性。1969年 Smith等首先报道的由超滤代替沉淀池技术，不但提高丁污水处理的效果和效率，而且大幅减少了剩余污泥产量，较传统的污水生化处理技术最示出明显优势，因而成为研发热点。MBR技术经过此后的不断完善，技术优势在工业应用中进了验证，膜池MISS质量浓度达到8000~14000 mg/L、剩余污泥量减少1/3以上、占地减少1/2~2/3等。因而到20世纪90年代，在美国、日本等发达国家的市政污水处理、工业污水处理等领域得到了推广应用。', '减少废水处理的难度：石化企业生产过程中，污水中含有高浓度的有机物和氨氮等难以降解的污染物，废水处理难度大。剩余污泥减量生化技术可以在废水处理的过程中减少污泥的产生，从而降低废水处理的难度。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE010', '中国', '石化', '污水适度处理回用', '末端处理', '污水适度处理回用是指达到国家环保排放标准的外排污水再经过生化、消毒、除味、脱色、过滤等技术措施，使水质得到适度改善而达到回用循环冷却水水质标准，回用到循环系统后采用水质稳定技术使设备腐蚀、结垢、生物黏泥等水质问题得到有效控制的过程。因此，污水适度处理回用包括两方面技术：一是改善水质技术，二是稳定水质技术。', '节约淡水资源：适度处理后的污水可以被用于一些不需要高质量水的生产环节，如冷却水循环、消防用水等，从而减少了对淡水的需求，节约了淡水资源。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE011', '中国', '石化', '污水深度处理回用', '末端处理', '污水深度处理回用是指对达到国家环保排放标准的外排污水，采用生化、氧化、絮凝过滤、活性炭吸附、超滤（UF）、杀菌、反渗透（RO）、混床等组合工艺进行处理，得到脱盐水的技术。污水深度处理回用包括两方面含义：一是采用去除污染物技术，将污水中大部分污染物去除，使污染物降低到接近新水指标，即污水的预处理；二是采用反渗透技术进行脱盐，将99%左右的盐脱除，得到准一级脱盐水，再经混床精处理，使水质达到锅炉给水标准并回用于锅炉。', '节约淡水资源：深度处理后的污水可以被用于需要高质量水的生产环节，如蒸馏、冷凝、洗涤等，从而减少了对淡水的需求，节约了淡水资源。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE012', '中国', '石化', 'MCI（RICH）最大量提高催化柴油十六烷值的加氢技术', '生产过程', 'MCI（RICH）是一种高效催化剂，能够在中低温下催化裂化重质烃，产生高质量的轻质烃和低芳烃柴油。它能够显著提高催化柴油的十六烷值，同时降低芳烃含量和硫含量。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE013', '中国', '石化', 'FDAS柴油两段加氢深度脱硫脱芳技术', '生产过程', '该技术的概况：FDAS（Fixed-bed Two-stage Hydrodesulfurization and Aromatics Saturation）柴油两段加氢深度脱硫脱芳技术是一种新型柴油加氢脱硫技术。该技术采用两段反应器，第一段反应器采用固定床反应器，主要用于深度脱硫，第二段反应器采用液相反应器，主要用于脱芳。该技术可以同时降低柴油中的硫含量和芳烃含量，提高柴油的品质。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE014', '中国', '石化', 'MHUG中压加氢改质', '生产过程', '概况：该系列技术主要是采用高效催化剂，通过加氢反应将重质原油中的杂质、硫、氮、芳烃等物质转化为清洁低凝柴油。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE015', '中国', '石化', '生产清洁低凝柴油的系列加氢技术(包括FHDW、FHUG-DW和FHI等)', '生产过程', '该技术的概况和主要参数是：该技术采用系列加氢反应器，通过加氢反应将原油或重油中的杂质和不饱和烃等有害成分去除，从而生产出清洁低凝点的柴油。主要参数包括反应器温度、压力、氢气流量和催化剂种类等。', '在石化中，该技术可以显著降低柴油中的硫、氮、多环芳烃等有害成分含量，从而减少柴油燃烧排放对环境的影响，提高燃油利用效率，降低机械设备维护费用。', '能源节约及清洁化；大气污染防治', '污染防治；节约资源；节能降耗', '中国石油化工股份有限公司/中国石油天然气集团公司/中海油能源发展股份有限公司', null, null, null, null, null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE016', '中国', '石化', '高柴油收率的FDC单段加氢裂化技术', '生产过程', '概况：FDC单段加氢裂化技术是指在一段反应器内，采用氢气作为催化剂，对重质石油馏分进行加氢裂化，从而实现高柴油收率的目标。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE017', '中国', '石化', 'RTS柴油深度脱硫工艺技术', '生产过程', '该技术的概况和主要参数是：RTS柴油深度脱硫工艺技术采用催化剂在高温高压条件下将柴油中的硫化物转化为可被轻松脱除的硫化氢，从而实现深度脱硫。该工艺的主要参数包括反应器温度、压力、氢气流量、催化剂种类等。', '在石化中，该技术可以显著降低柴油中的硫含量，从而减少柴油燃烧排放对环境的影响，同时提高燃油利用效率和机械设备的使用寿命。', '能源节约及清洁化；大气污染防治', '能源节约及清洁化；大气污染防治', '中国石油天然气集团公司/中国石油化工股份有限公司/中国石化集团公司', null, null, null, null, null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE018', '中国', '石化', '高芳烃LCO加氢转化生产高价值石脑油技术(包括FHC-LCO和FD2G)', '生产过程', '该技术的概况：通过将低价值的LCO（Light Cycle Oil，轻质环烷烃馏分）加氢转化，生产出高附加值的石脑油产品，以提高原油加工厂的经济效益。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE019', '中国', '石化', 'FSDS串联反应器超深度脱硫技术', '生产过程', '该技术主要采用串联反应器结构，利用高效催化剂，通过加氢反应实现柴油中硫化物的脱除。其中，第一级反应器实现前段的脱硫，第二级反应器实现后段的脱硫，从而实现超深度脱硫。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE020', '中国', '石化', '柴油加氢新工艺技术', '生产过程', '柴油加氢是一种石化加工技术，通过将原油中的低质量馏分进行加氢处理，提高其质量和降低硫、氮等杂质含量，从而获得更高品质的柴油产品。该技术的主要参数包括加氢反应温度、压力、氢气流量、反应时间等。', '柴油加氢新工艺技术可以有效地提高柴油的品质，降低排放污染物的含量。它可以降低柴油的硫、氮、磷等有害物质含量，提高柴油的辛烷值和密度，并减少烷值分布的不均匀性，使得柴油的燃烧更加完全，减少废气排放和对环境的污染。', '资源高效利用；能源节约及清洁化；大气污染防治；水污染防治', '污染防治；节约资源；节能降耗', '中国石油化工股份有限公司/中国石油天然气集团公司/中海油能源发展股份有限公司/中国海油集团有限公司', null, null, null, null, null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE021', '中国', '石化', 'S-RASSG柴油超深度脱硫催化剂选择及级配技术', '生产过程', '该技术的主要参数包括催化剂选择、催化剂级配比例和反应温度等。通过选用适当的催化剂和调节反应条件，可以实现对污染物SOx的高效去除，降低柴油的硫含量，提高柴油的质量，同时也能够减少对环境的污染。', '在石化行业中，该技术的效果显著。它可以有效减少石化生产过程中排放的SOx污染物，达到大气污染防治的目的。同时，该技术也可以提高石化产品的质量，满足市场需求。', '能源节约及清洁化；大气污染防治', '污染防治；提升品质', '中国石油化工股份有限公司/
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE022', '中国', '石化', 'SRH柴油液相循环加氢技术', '生产过程', '概况：该技术采用液相循环加氢反应器，通过加氢反应将重质原油中的杂质、硫、氮等物质转化为清洁低凝柴油。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PE023', '中国', '石化', 'FD2G高芳烃催柴加氢转化生产高辛烷值汽油技术', '生产过程', '该技术主要参数包括选择合适的催化剂、催化剂的载体、反应温度和压力等。通过适当的调节这些参数，可以实现对原油中高芳烃的加氢转化，生产出高质量、高辛烷值的汽油产品。', '在石化行业中，该技术的效果显著。它可以提高石化产品的质量，生产出更高辛烷值的汽油，同时也能够减少对环境的污染，达到大气污染防治的目的。', '能源节约及清洁化；大气污染防治', '污染防治；节约资源；节能降耗；提升品质', '中国石油化工股份有限公司', null, null, null, null, null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP001', '中国', '造纸', '深度脱木素技术', '生产过程', '深度脱木素技术中提高木素选择性的途径主要是通过保护碳水化合物和提高脱木素速率实现的技术突破，其内部发展的冷喷放技术和锅内高温逆流洗涤技术大大的改善了纸浆的强度特性，提高了洗涤效率，简化了洗浆设备，从而针对余热和残碱加以利用，创新了深度脱木素技术的发展。', '既能使脱木素技术减少或避免剥皮反映，又能促进木素的降解和脱落，缩短蒸煮时间，提高蒸煮效率，以此方式增加纸张的强度。', '资源高效利用；能源节约及清洁化', '节能降耗；提升效率', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP002', '中国', '造纸', '锅炉烟气余热回收技术', '生产过程', '利用省煤器、多管式换热器等装置确保烟气热量的回收，通过加热锅炉补水、加热空气用作锅炉烟气余热保证的原辅助材料，保证节煤效果和锅炉的整体热效率。', '实现了热量的回收利用，减少了煤等能源的消耗。', '资源高效利用；能源节约及清洁化', '热量回收；降低煤耗', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP003', '中国', '造纸', '厌氧处理废水沼气利用技术', '末端处理', '厌氧处理废水沼气利用技术通过对传统废水处理装置进行改进，增加厌氧与沼气处理系统。', '由于造纸废水中含有较丰富的有机物，采用厌氧工艺处理造纸废水，可以实现造纸废水沼气的回收利用。', '水污染防治；资源高效利用', '沼气处理；沼气回收利用', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP004', '中国', '造纸', '造纸污泥的生物精炼', '末端处理', '造纸污泥去灰分之后，在生物乙醇制备方面有很大潜力，硫酸盐法制浆得到的造纸污泥可作为丁醇生产的原料；造纸污泥中的有机固体废弃物进行厌氧消化处理用以破坏病原菌和产生再生能源—沼气的同时, 还避免了与有机废弃物处理的相关问题。', '实现了污泥资源化利用的同时，能够产生再生能源沼气，减少了有机废弃物的处理这一环节，做到了清洁生产。', '固体废弃物处置及资源化；资源高效利用', '节能降耗；污泥资源化', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP005', '中国', '造纸', '造纸污泥填料化', '末端处理', '造纸污泥在制备黏土砖、建筑涂料等方面已具有一定研究进展。', '将污泥用于其他领域的原料，实现了污泥的资源化利用，同时减少了固体废弃物的产生。', '固体废弃物处置及资源化；资源高效利用', '减少固废；污泥资源化', null, null, null, null, null, null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP006', '中国', '造纸', '电机变频改造', '生产过程', '相关电机是根据纸机的最大生产能力设计的, 而生产中客户对于宽幅、克重等的不同需求, 造成电机实际功率需调整, 传统电机只能调整转速, 但电耗是基本相同的。采用变频调速调节方式取代传统的阀门调节, 减少了电机的启动电流, 节电效果明显, 并且改善流量, 流速和压力控制精度, 提高产品质量。', '通过调节电机达到减少启动电流的目的，实现清洁生产和电力资源的节约，减少温室气体CO2的排放。', '资源高效利用；能源节约及清洁化', '电力节约；碳减排', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP007', '中国', '造纸', '锅炉加装分层给煤装置', '生产过程', '造纸企业使用的正转链条锅炉采用普通给煤方式, 存在燃烧不完全, 着火条件差, 灰渣可燃物含量高等缺点。锅炉分层给煤装置, 由给煤、调煤、分层、传动等部分组成, 位于炉排进煤处, 按照生产负荷调整给煤量煤经过播煤辊和筛分器被筛分成大小块和煤粉由下而上分层布于炉排, 煤层疏松、均匀, 透气性好。', '改善燃烧工况, 减小鼓风阻力, 增大煤与空气接触, 大块于下层易燃尽, 煤粉于上层减少漏煤, 节煤效果明显。', '资源高效利用', '降低煤耗；提高燃烧效率', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP008', '中国', '造纸', '烘缸冷凝水回收', '生产过程', '多数企业纸机烘缸冷凝水经过管道汇集到水罐内作为锅炉补充水, 少数企业甚至完全不回收烘缸冷凝水直接排掉, 造成热能的极大浪费。采用冷凝水回收机对热量进行回收, 通过疏水装置将冷凝水引入集水罐, 再经消气蚀处理使高温冷凝水打入汽包, 成为一个闭式循环系统, 凝结水的能量直接回收到锅炉里。', '通过采用冷凝水回收机对热量回收，凝结水的回收温度仅丧失在管网降温部分, 节煤及软化水效果明显。', '资源高效利用', '节约水耗；热量回收', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP009', '中国', '造纸', '超效浅层气浮处理废水', '末端处理', '以原生木浆、废纸为原料的造纸企业, 其废水含悬浮物比例很高。超效浅层气浮技术在污水引入大量微小气泡, 通过表面张力气泡粘附在细小悬浮物上, 形成整体比重小于水的絮凝物, 密度差使悬浮物浮于水面形成浮渣, 从而实现固液分离。', '流失纤维在此过程中回收, 最大限度地循环使用。配合添加絮凝剂, 悬浮物去除率可达90%以上。不仅降低废水中的悬浮物浓度, 还可去除部分溶解性COD、BOD, 极大的提高水循环利用。', '水污染防治', '废水回用；废水资源化', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP010', '中国', '造纸', '制浆废水回用', '末端处理', '废纸为原料制浆产生的废水，主要是浆料洗涤、设备冲洗产生的，主要成分为 SS和COD，且COD含量较低。经适当处理，上清液可以继续用于制浆生产中。', '采用废水的循环利用，可以降低制浆过程的取水量和废水产生量。', '水污染防治；资源高效利用', '废水回用；废水资源化', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP011', '中国', '造纸', '提高白水循环利用率', '末端处理', '在满足生产系统用水的前提下，多余白水回用于制浆系统和纸机对水质要求不严格的生产过程。成形脱水区产生的浓白水回用于机前纸浆的稀释，高压脱水产生的稀白水回用于制浆过程以及除渣过程中。', '通过生产系统废水的循化利用，达到减少和降低取水量和废水产生量的目的。', '水污染防治；资源高效利用', '废水回用；废水资源化', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP012', '中国', '造纸', '浓缩机改造', '生产过程', '使用新式节能型压力筛，动力消耗低，与老式圆网浓缩机相比，同等筛选面积的消耗功率偏小，单位时间内出浆快。', '通过对浓缩机进行新型改造，实现了浓缩机的低动力消耗，节能效果明显。', '能源节约及清洁化', '节约电耗；低动力消耗', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP013', '中国', '造纸', '污泥机械脱水', '末端处理', '造纸污泥以板框压滤和带式压滤为主，也有企业开发了连续式污泥深度脱水设备。', '实现污泥与水的分离，分别进行进一步的水处理与固体废弃物处理。', '水污染防治', '废水处理；污泥分离', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP014', '中国', '造纸', '白水处理技术', '末端处理', '气浮法处理白水是目前白水回用中用得较多的一种回用技术，其中的常规溶气气浮法很早就用于处理造纸白水，后来又开发出射流气浮白水回用技术。目前用得较多的是超效浅层气浮技术；真空过滤法处理白水也有广泛的应用，近年来国内的一些大型造纸企业大力推广真空过滤机用于白水处理，提高白水的处理与循环回用的程度；造纸白水先经微孔过滤处理回收纤维，降低白水中的悬浮物含量，在加混凝剂和助凝剂，使白水中的细小纤维、填料、胶体性物质及部分溶解性有机物聚沉（化学混凝法），处理后的澄清水可回用于生产过程。膜分离技术具有处理效果好、能耗低、占地面积小、操作管理容易等优点。但其处理水量的能力不大、费用较高。', '实现了清洁生产和白水的资源化回收利用，减少水中污染物排放。', '水污染防治', '废水处理；废水资源化', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP015', '中国', '造纸', '热泵二次蒸汽利用', '生产过程', '采用热泵系统，将烘干部新鲜蒸汽直接通汽方式， 改为节能型的热泵供汽方式。', '充分利用二次余热蒸汽，节约新鲜蒸汽用量，起到降低蒸汽的耗用量', '资源高效利用；能源节约及清洁化', '蒸汽回收利用；节约热能', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP016', '中国', '造纸', '密闭汽罩回收余热', '生产过程', '对现有烘干部进行改造，采用密闭汽罩，提高烘缸热效率。', '采用此技术能够降低热损失，节约蒸汽，以达到节能降耗的目的。', '资源高效利用；能源节约及清洁化', '蒸汽回收利用；节约热能', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP017', '中国', '造纸', '黑液资源化', '生产过程', '提取造纸黑液中的木质素，经过改性、磺化合成木质素磺酸钙粘合剂。', '通过此技术提取废弃物中的木质素，实现其资源利用，降低生产成本，减少污染。', '资源高效利用；能源节约及清洁化', '木质素资源利用；降低成本', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP018', '中国', '造纸', '干湿法备料和连续蒸煮', '生产过程', '把原来的干法备料改为干湿法备料，其次改原来的间歇蒸煮为连续蒸煮，最后拆除掉蒸煮麦草的所有蒸球，用连续蒸煮系统代替。', '该技术能够解决干法备料中产生粉尘乱飞的问题，降低备料期间的噪声污染，同时提高草片的质量。以达到耗能低、污染少、保证纸浆质量的目的。', '资源高效利用；噪声污染防治', '减少粉尘；降低能耗', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP019', '中国', '造纸', '提取车间的封闭式筛选', '生产过程', '改变原来的开式筛选为封闭式筛选，在提取车间增加双辊挤浆机，把漂白浆中的黑液过滤出来，大大提高黑液提取效率。', '通过该技术，能够提高对黑液的提取效率，降低耗水量。', '水污染防治；资源高效利用', '废水处理；节能降耗', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP020', '中国', '造纸', '变频磨浆', '生产过程', '用变频技术把粗磨和精磨分开，利用其他生产线耗电较小的精磨设备，优化组合生产流程，在保证浆料质量的同时，为主体设备腾出提高产量的空间，从而实现增产降耗。', '通过区分粗磨和精磨，优化生产路线，合理利用设备，减少电耗，提高产量。', '资源高效利用', '生产流程优化；增产降耗', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP021', '中国', '造纸', '中浓打浆', '生产过程', '利用纤维间的内摩擦效应，可以使打浆电耗节省30％-40％。', '降低电耗，降低电力成本，减少打浆过程中产生的污染。', '能源节约及清洁化', '降低电耗；减少成本', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP022', '中国', '造纸', '本色麦草浆清洁制浆技术', '生产过程', '麦草经切断、筛选除尘后进入蒸煮器，在高温环境中与蒸煮化学药品发生化学反应，绝大多数木素及部分半纤维素被溶出，分离出的纤维素(含少量未溶出的半纤维素)经后续的机械疏解、氧脱木素、洗涤、筛选净化过程得到纸浆用于纸张的生产。溶出的木素及部分半纤维素被作为废液进入资源化处理系统。', '(1)降低纤维原料消耗10%；(2)提高除尘效果10%；(3)减少化学药品消耗5%；(4)降低蒸汽消耗20%；(5)节约清水用量50%；(6)提高黑液提取率，黑液提取率>90%；(7)降低生产成本，实现废液资源化利用；(8)不产生AOX和二噁英。', '资源高效利用；能源节约及清洁化', '降低原料消耗；提高生产效率', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP023', '中国', '造纸', '置换蒸煮工艺', '生产过程', '置换蒸煮系统包括预浸装料、初级蒸煮、中级蒸煮、升温/保温、置换回收、冷喷放等工艺步骤。通过对常规立锅间歇蒸煮进行技术改造后实施该技术，可以得到强度高、卡帕值波动小的浆料，同时浆料质量均匀，有利于减少后续漂白过程化学药品用量，降低中段水污染负荷。', '(1)消除废气喷放对空气的污染；(2)减少进入漂白工段的木素含量；(3)降低漂白废水中的可吸收有机卤化物(AOX)产生量，减少漂白废水AOX排放量20%；(4)减少漂白药品消耗；(5)节省蒸汽消耗，蒸汽消耗量减少至0.55～0.75吨/吨浆。', '资源高效利用', '降低原料消耗；提高生产效率', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP024', '中国', '造纸', '氧脱木素技术', '生产过程', '氧脱木素是蒸煮脱除木素过程的延伸，蒸煮所得到的纸浆经过筛选、洗涤之后，进入氧脱木素系统。氧脱木素反应所用碱来自氧化白液，所用氧来自空气，洗涤水可用蒸汽冷凝水，滤液直接逆流进入碱回收，降低水耗和化学药品的消耗。主要设备有氧反应塔、刮料器、混合器与加热器。氧脱木素系统一般包括中浓浆泵、混合器、反应塔、喷放塔和洗浆机等。对木浆和竹浆，适当提高蒸煮之后纸浆的硬度，然后通过氧脱木素降低浆的硬度，可获得较高的得率。', '(1)提高木素脱除率；(2)降低纸浆卡伯值40%～50%，以满足现代环保型ECF或TCF漂白的要求；(3)提高制浆得率，降低原料成本；(4)降低漂白污减少漂白废水AOX产生量50%～60%；(5)节约漂白化学品消耗40%～50%，减轻漂白废水污染负荷，COD排放量降低40%。', '资源高效利用', '减少原料消耗；降低污染负荷', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP025', '中国', '造纸', '无元素氯漂白技术', '生产过程', '采用二氧化氯在中浓度(10%～16%)条件下对纸浆进行漂白，取代氯气和次氯酸盐漂白。二氧化氯不含分子氯，漂白废水的AOX比有氯漂白大大降低，而且二氧化氯在破坏木素但不显著降解纤维素或半纤维素方面有很高的选择性。增加二氧化氯发生器、提升改造漂白主要工艺设备，对漂后洗浆设施进行防腐改造等。减轻漂白废水的污染负荷，改造后可不再新增二噁英的排放，减少化学品的消耗。', '(1)取消元素氯漂白；(2)降低漂白废水的污染负荷，减少漂白废水中50%的COD和80%A O X产生量；(3)在提高纸浆白度的同时，改善漂白浆的强度；(4)降低用水量，减少漂白废水40%。', '资源高效利用', '减少原料消耗；降低污染负荷', null, null, null, null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP026', '中国', '造纸', '镁碱漂白浆化机生产关键技术', '生产过程', '对国外先进、成熟化机浆生产技术再创新，使之适应我国化机浆应用需求和生产线特点，利用镁碱(或直接利用Mg(OH)2)部 分替代烧碱和硅酸钠，用于各类漂白化机浆生产，在基本不影响成浆质量指标条件下，镁碱替代率达到30%～50%。', '(1)降低COD产生量，吨浆COD产生量降低30%～50%；(2)减少SS排放量，吨浆SS排放量下降15%以上；(3)提高制浆得率，吨浆节约木质资源约210～ 300m3 ；(4)有效缓解高浓磨、螺旋压榨等设备结垢问题；(5)解决碱回收法化机浆废液处理硅干扰问题。', '资源高效利用', '减少原料消耗；降低污染负荷', null, null, '该技术在工程运行过程中保持稳定，对环境、技术参数等无干扰。', null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP027', '中国', '造纸', '白水循环综合利用技术', '生产过程', '造纸白水循环综合利用技术包括合理的生产工艺、合适的设备、智能化的DCS模拟控制系统和生产系统的节能优化方案几部分组成。根据不同浆料、不同填料、抄速、纸机幅宽和所产品种等多因素，综合考虑，协同利用，达到白水的高效利用。利用纸机白水代替清水，减低清水使用和能量消耗。', '(1)提高白水循环利用水平，降低造纸用水量10%～40%，吨纸水耗可以控制在小于10吨；(2)纤维填料留着率提高至95%以上；(3)纤维与填料节省10%～50%', '资源高效利用', '减少水耗；降低原料消耗', null, null, '该技术在工程运行过程中保持稳定，对环境、技术参数等无干扰。', null, '调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP028', '中国', '造纸', 'RDH蒸煮技术', '生产过程', '当蒸煮结束时，蒸煮国内热黑液用洗浆机的滤液，也就是用冷黑液进行置换，置换出来的黑液根据温度不同被分别储存在不同的黑液槽中，用于下一次蒸煮时原料和蒸煮白液的预热。蒸煮好的纸浆在蒸煮锅内至少完成1次洗涤，并被冷却下来，然后用压缩空气将其喷放或泵抽至喷放锅。', '（1）RDH蒸煮周期更小；（2）比常规法蒸煮大大减少了新鲜蒸汽用量，可节约70%~80%的蒸汽；（3）减少碱耗，提高纸浆得率；（4）未漂浆和漂白浆强度都高出10%以上。', '资源高效利用', '蒸汽资源利用；减少碱耗', '广东鼎丰纸业', null, '此套设备和技术已经实现产业化，运行良好，性能稳定，已实现大规模应用。', null, '书籍P55', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP029', '中国', '造纸', '棕榈果串高得率浆制高强包装纸和纸板技术', '生产过程', '通过采用自主研发的10万吨级棕榈空果串高得率清洁制浆成套关键技术，可以高值利用东南亚储量丰富的棕榈空果串纤维制造高强本色包装纸和纸板产品。缓解当地造纸工业纤维原料短缺状况，取得经济、社会和环境生态协同效益。', '（1）采用清洁生产工艺提高原料利用率，实现了木质素的最大化利用；（2）生产所用锅炉采用生物质锅炉，不增加当地的碳排放。', '资源高效利用；温室气体减排', '碳减排；原料最大化利用', '马来西亚、印度尼西亚等地区', '技术投资：10万吨级棕榈果串高得率浆制高强瓦楞原纸新建工程总投资35600万元，其中设备投资费18200万元，建筑工程费6057万元，安装工程费2326万元，征地费用2400万元，其他工程及预备费9017万元。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP030', '中国', '造纸', '造纸碱回收白泥渣生产多孔硅酸盐滤料', '末端处理', '本技术针对造纸白泥高含水率、高碳酸钙、重金属浸出等问题，将造纸白泥与水泥混合，进行固化成型反应脱水，可大大降低造纸白泥煅烧入窑含水率，形成低温煅烧技术。经过煅烧后造纸白泥中的碳酸钙可作为水热合成的钙质原料，将其与粉煤灰、石英尾泥等硅质原料混合，经造粒成球、自养、蒸养后得到多孔硅酸盐滤料。', '该技术对固废进行资源化利用，减少了造纸白泥固废的堆积和填埋，实现了造纸白泥的资源化、减量化和稳定化。', '固体废弃物处置及资源化', '固废资源化；固废减排', '浙江中劲环保科技有限公司', null, '此套设备和技术已经实现产业化，运行良好，性能稳定。', null, '成书彩稿P273', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP031', '中国', '造纸', '管道式分离技术及设备', '生产过程', '管道式分离技术是通过T型管、柱形旋流管、轴向旋流管的多种组合，以适应不同流量、不同含率、不同油品、不同工况、不同处理要求的各种实际使用情况。管道式分离技术的主要优点就是体积小、重量轻、占地面积少，分离效率高，工艺流程简单、操作简便，已成为系统的适用于陆地、海洋平台及深海海底、采油井井底等各种使用环境和条件油田采液处理新技术。可用于造纸行业中的多相分离。', '在新建工程中，可以减少征地面积和大型罐建造数量，极大节省投资费用；在老站扩容改造工程中，可以快速灵活接入原有流程，有效提高原有处理能力，改善出水水质，并可降低药剂使用和加热费用。', '资源高效利用', '降低投资；提高效率', null, null, '该技术在工程运行过程中保持稳定，对环境、技术参数等无干扰。', null, '成书彩稿P136', '也可用于其他行业');
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP032', '中国', '造纸', '低品味余热发电技术', '源头控制', '本技术采用改进的有机朗肯循环系统（ORC）设计技术和关键设备设计技术，通过利用温度较低，流量较小的余热资源，包括地热、光热以及工业余热，形成高温高压的有机工质蒸汽，推动透平机膨胀做功，驱动发电机发电，实现不稳定热源及低品位余热高效利用。现已建立自主知识产权的ORC仿真模拟系统，可对系统进行全局优化，获得最佳工艺参数；同时建立较为完整工质物性数据库和优选系统，能针对特定的低温体系，筛选最为合适的工质，针对不同资源和参数的余热，开发出高速、紧凑、一体式的小型发电机组。', '针对不同企业产生的大量低值或废气的预热，设计开发特定的有机朗肯循环系统进行发电，将多余的热能转化为电能，实现能源多级高效利用，具有一定的经济效益和社会效益。', '能源节约及清洁化', '减少电耗；热能高效利用', null, null, '该技术在工程运行过程中比较稳定，对环境、技术参数等干扰较小。', null, '成书彩稿P137', '也可用于其他行业');
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP033', '中国', '造纸', '30万吨/年废纸中浓制浆关键设备研制及系统成套集成', '生产过程', '该技术研制的转鼓式碎浆机通过内部提料板及隔仓板的结构优化，碎浆浓度高达21%，纤维流失率小于0.8%；通过转子及筛壳底部结构的创新设计，研制的中浓压力筛筛选浓度可达4%（粗筛）。', '使用30万吨/年废纸中浓制浆项目不仅可以节能降耗，提高工效，还可以减少污水排放量，降低成本，可获得一举多得的效果。', '资源高效利用', '节能降耗；污水减排', '东莞金田纸业', '以30吨/年废纸中浓制浆生产线为例，技术寿命为5-10年，所需主要设备为BFW2000链板输送机2台，ZDG400鼓式碎浆机2台，ZSA6B高浓浓除渣器2台，ZNS87中浓压力筛2台，ZNS84中浓压力筛2台，ZDF4B纤维分离机2台，RSH-2排渣分离机2台，ZSA6B中浓除渣器2台，ZNS87中浓分级筛2台一段除渣器2套，二段除渣器2套，三段除渣器2套，四段除渣器2套，ZNS86中浓一段精筛2台，ZNS84中浓二段精筛2台，盘式真空过滤机4台，纸机1台，所需投入资金23000万元。', '该技术在工程运行过程中保持稳定，对环境、技术参数等干扰的敏感程度较低。', null, '成书彩稿P149', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP034', '中国', '造纸', '卫生纸机毛布在线清洁技术', '生产过程', '造纸毛毯因其价格较昂贵，在纸张生产成本中占有一定的比例，所以是影响纸厂效益的重要因素之一。该技术解决了毛布脏污引起的毛布寿命、能耗、车速及质量问题。毛布寿命从不到两个月延长到150天以上（最高226天）；通过保洁清洗，保证毛布在整个寿命期内，清洁状况良好。同时，减少了毛布停机清洗的时间；有效减少真空负载。', '1. 解决了毛布脏污引起的毛布寿命、能耗、车速及质量问题。毛布寿命从不到两个月延长到150 天以上（最高226 天）； 2. 通过在线的保洁清洗，保证毛布在整个寿命期内，清洁状况良好。同时，减少了毛布停机清洗的时间； 3. 因毛布清洁状况改善，真空效率提升，有效减少真空负载； 4. 因真空负载降低，传动阻力下降，纸机传动负载得以降低，减少传动电耗； 5. 因毛布清洁状况获得改善，毛布透水性改善，有效提升压榨脱水效果，降低纸页上缸水分，节省蒸汽耗用成本。', '资源高效利用', '降低负荷；减少电耗', '维达纸业（浙江）有限公司', '技术投资：总费用约42万元，加上管道、阀门、仪表、自控系统，总投资约60万元。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP035', '中国', '造纸', '商业智能系统开发项目', '末端处理', '目前，纸张多采用BOPP 等塑料进行表面覆膜，以保证纸张的美观和使用。但表面覆膜的纸张在回收利用时给纸浆分离增加了很大的难度，导致废纸回收利用率低。其中，回收过程面临的主要问题如下：较大的塑料碎片不利于连续水相中的流动和收集，容易造成纸浆剥落不完全；较小的塑料碎片会导致纸浆与塑料无法分离，增加能耗，影响分离产品的质量和使用效果。因此，该技术利用智能系统控制塑料碎片的大小，找到最佳的纸浆分离效果。破碎机采用内嵌式设计，即在内桶上和动轴上嵌有定刀和动刀，根据不同的物料性质和回收要求，其转速、形状、尺寸大小、刀片安装位置、大小与形状均可以变化；旋转刀片形状为弧形，刀片长度为485mm、宽度为lOOmm、厚度为22mm；固定刀片形状为条状，刀片长度为495mm、宽度为llOmm，厚度为25 mm；刀片高速运转，转速为960r/min；破碎进料室有分开安装的筛网和缓冲室。', '（1）破碎机内嵌式设计可满足破碎尺寸要求，保证破碎效果。（2）缓冲室可避免喂料时的泼撒，机器噪音低，安全可靠。（3）大块塑料切割成10～125px 的长条碎片，纸浆脱离效果最佳。（4）破碎机刀片的高转速运行可实现对固体废弃物的再粉碎，并在强力机械作用帮助下实现一定尺寸的小塑料碎片与纸浆纤维的水中分离。', '固体废弃物处置及资源化', '智能化；固废减排', null, null, '此设备作为环保流水线主体设备的关键设备，提供给公司客户，运行安全可靠，实现了纸浆与塑料的有效分离。', null, 'https://www.greentechbank.com/greentech/web/achv/achivementDetail/b5c09ece6fdc47edbe0017822fbb31c0', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP036', '中国', '造纸', '造纸污泥转化成环保吸收剂', '末端处理', '造纸污泥（来自造纸业）经适当处理后能形成一种高效光吸收材料，可悬浮在水面，有效地吸附疏水性物质（植物油、合成和矿物油、动物脂肪、燃料、有机物质）。经适当应用后，甚至能吸附水面的灰尘。', '实现了污泥资源化利用的同时，能够产生再生能源沼气，减少了有机废弃物的处理这一环节，做到了清洁生产。', '固体废弃物处置及资源化；资源高效利用', '节能降耗；污泥资源化', null, null, '此设备作为环保流水线主体设备的关键设备，提供给公司客户，运行安全可靠，实现了纸浆与塑料的有效分离。', null, 'https://www.greentechbank.com/greentech/web/achv/achivementDetail/eb5934efc8584bcdbebe1b4c83cbedca', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP037', '中国', '造纸', '造纸废弃物再回收设备', '末端处理', '该设备能够高效率实现造纸废弃物再回收。造纸废弃物再回收的设备主要包括破碎机、搓揉机、漂洗槽、离心分离机。
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP038', '中国', '造纸', '单张纸高端印刷机关键技术', '生产过程', '单张纸高端印刷机是指具有高速、高精度、高效率的多色印刷机，是该技术突破了与高速纸张控制有关的传递、检测、时序控制等关键技术，发明了高速纸张输送、传递、交接技术，研制了光纤限定反射和超声波技术相结合的高速纸张检测系统，实现了精准的纸张时序控制；突破了墨层控制高精度、强实时、宽印刷幅面等关键技术，研制了国内首套高性能自动墨量控制系统；攻克了高精度印刷过程控制涉及的自动套准、间隙调整、散尾变形、水墨平衡等关键技术，发明了适应厚纸印刷和最少纸张交接次数的滚筒排列结构，研制了不依赖滚筒倾斜调整的印版斜向调整装置，；攻克了印刷机械特性匹配、故障诊断、网络控制等关键技术，开发了符合CIP3/CIP4 国际标准且支持印刷企业管控一体化架构的数字化、网络化、智能化印刷流程系统，可实现印刷作业全过程自动化，大幅提高印刷效率。 ', '该技术的应用，极大地促进了印品质量和印刷效率提高、材料浪费减少、成本降低，使中国印刷向绿色印刷发展，从整体上推进了中国印刷机及印刷行业的科技进步。', '资源高效利用', '智能化印刷；降低成本；提高生产效率', '本成果在国外著名印刷机制造企业得到应用。使用本成果的印刷企业超一千家，并出口到日本、俄罗斯、南非、巴西等国家。', null, '此套设备和技术已经实现产业化，运行良好，性能稳定，生产线生产能力强。', null, 'https://www.greentechbank.com/greentech/web/achv/achivementDetail/3fd0452e608d4409b9b43f8e98b2370b', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP039', '中国', '造纸', '粉煤灰综合利用技术', '末端处理', '该技术的原理与技术关键是把外营力自然成岩理论应用到生产粉煤灰建筑材料中去，它的创造点是利用造纸废水中的废弃物，其中含有大量的表面活性剂和碱性物质，其掺入后对粉煤灰的凝聚、集结起到积极的作用，从而激发了粉煤灰的潜在活性，同时也产生了一种结构性防水、憎水性能。是一种新型节能、利废的建筑材料。', '将造纸废水中的废弃物与粉煤灰混合形成建筑材料；实现了废弃物的资源化利用，同时减少了固体废弃物的产生。', '固体废弃物处置及资源化；资源高效利用', '废弃物利用；粉煤灰资源化', null, null, '该技术在工程运行过程中保持稳定，对环境、技术参数等无干扰。', null, 'https://www.greentechbank.com/greentech/web/achv/achivementDetail/84428fca3cc34c68af4f7a762b268439', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP040', '中国', '造纸', '生物制浆技术', '生产过程', '生物制浆是直接利用微生物降解纤维原料中的木素，解离出纤维，使之成为纸浆。', '该技术具有环境友好性，可降低能耗和化学品用量，因此将生物技术作用于制浆造纸过程，有利于克服化学法所存在的缺点如高化学品消耗、高污染负荷等，从而降低纤维原料用量和废水污染负荷。', '能源节约及清洁', '生物技术；减少污染', null, null, '该技术具有很高的发展潜力，但目前仍处于研究阶段，稳定性一般，距离应用于实际生产还有一定差距，应加大投资，争取早日将生物制浆技术投人产业化生产。', null, 'http://www.paper.com.cn/news/daynews/2017/17040607250667194.htm', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP041', '中国', '造纸', '高得率制浆技术', '生产过程', '高得率制浆可提高资源利用率，减少纤维和化学药品的消耗，采用不产生有机氯化物的新漂剂，能使漂白废水中的BOD,COD和AOX等含量比化学浆少得多，生产过程排放废液的污染负荷轻，废液的回收处理比较简单。例如APMP的制浆得率一般都在85%左右，生产过程中仅有原料量15%的溶出物进入制浆废水中，因此，相对于化学浆(得率为40%~50%)来说，其污染负荷要低得多。', '高得率技术制得的浆中所含废水80%~90%通过螺旋压榨脱水机挤压后可回用，污染负荷的排放量低于其发生量。且排放的废水具有可生化性，采用生物处理最终可达标排放。同时高得率浆具有再制浆的优良性能，有利于废纸的回收再利用。', '资源高效利用', '废水回用；降低污染负荷', null, null, '该技术在工程运行过程中比较稳定，对环境、技术参数等干扰较小。', null, 'http://www.paper.com.cn/news/daynews/2017/17040607250667194.htm', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('PP042', '中国', '造纸', '全无氯漂白技术', '生产过程', '全无氯漂白是无元素氯漂白技术的进一步发展，用不含氯的物质如H202、O2、O3等作为漂剂在中高浓条件下对纸浆进行漂白。无元素氯漂白产生的废水一仍具有一定的微生物毒性，而全无氯漂白废水则检测不出毒性。', '采用全无氯漂白技术可从根本上消除有机氯化物的产生，改变漂白废水对环境的污染。漂白废水可以用作蒸煮后的洗浆用水和配蒸煮药液的稀释水，这样既可节约制浆用水，同时又回收利用废液中的碱基，获取废液中有机物的热能，达到节能增效的作用。', '资源高效利用', '降低水耗；节能增效', null, null, '该技术在工程运行过程中比较稳定，对环境、技术参数等干扰较小。', null, 'http://www.paper.com.cn/news/daynews/2017/17040607250667194.htm', null);
