INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE001', '中国', '纺织', '静电处理技术', '末端控制', '该技术适用于纺织热定型工序油烟废气处理。静电式除尘器的净化效率高，集油极上收集的油可收集后作为燃烧油使用。热定型机排放油烟宜采用三、四电场静电除尘器。对于温度达180℃~230℃的油烟需经冷却处理后，再进入静电除尘器。', '静电处理技术利用静电场使油烟颗粒带电，在电场力作用下向收尘极板移动而从气流中分离处理，从而达到净化烟气的目的。', '大气污染防治', '油烟废气/资源再利用', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE002', '中国', '纺织', '水喷淋+静电处理技术', '末端控制', '该技术适用于纺织定型机工序废气处理。水喷淋技术与静电处理技术的结合应用，即先进行水喷淋预处理再进行高压静电净化处理，一次性解决了静电处理的相关问题，又充分利用了水喷淋式处理工艺的优势。', '具备了废气降温、除毛屑、除蜡、除油、防火、易维护清理等功能。', '大气污染防治', '定型工序废气/资源再利用', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE003', '中国', '纺织', '小浴比前处理及染色技术', '生产过程', '低浴比染色技术通常是指使用气流染色机或低浴比溢流染色机进行染色，染液和织物可快速循环、升温和翻动，浴比可低至1：5（甚至1：3）。', '小浴比染色能够减少能源和水的消耗；降低盐和碱剂的用量；减少染料用量；有利于改善染料重现性和匀染性。', '资源高效利用', '小浴比/节能减排/节水降耗', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE004', '中国', '纺织', '丝光碱液加热浓缩回收技术', '生产过程', '通过加装扩容蒸发器对丝光废水中的液碱进行浓缩蒸发后回用。', '达到生产要求浓度的碱液进行回收重复应用于生产，从而减少液碱的流失并减少废水量。', '资源高效利用', '丝光废水/碱液回收', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE005', '中国', '纺织', '丝光碱液膜回收技术', '生产过程', '利用耐碱超滤膜对淡碱液进行过滤，去除杂质后再补充少量浓碱和纯水进行回用。', '达到生产要求浓度的碱液进行回收重复应用于生产，从而减少液碱的流失并减少废水量。', '资源高效利用', '丝光废水/碱液回收/耐碱超滤膜', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE006', '中国', '纺织', '氧漂水回用', '生产过程', '氧漂废水污染较轻，其中含有残余的漂白剂、少量醋酸、草酸、硫代硫酸钠等，且因杂质较低，而退煮工序使用的助剂中含有烧碱和双氧水，而这些试剂在布上的残留，不会对处理效果产生影响，因此氧漂废水可以回用于退煮工序充当水洗用水。', '可实现在节水的同时减少废水的排放量。', '资源高效利用', '氧漂水回用/节水减排', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE007', '中国', '纺织', '碱性印染废水脱硫技术', '生产过程', '利用碱性印染废水作为脱硫除尘液，中和酸性烟气，SO2去除率达到了75％，具有明显的环境和经济效益，所以热电厂尾气处理也可以作为用水节点，接受碱性废水。', '碱性印染废水可以有效中和印染集中区外的热电厂锅炉排放的大量酸性烟气，实现较好的脱硫效果的同时减少新鲜水的消耗。', '大气污染防治/资源高效利用', '烟气脱硫/碱性印染废水', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE008', '中国', '纺织', '生物酶应用技术', '生产过程', '生物酶是生物细胞产生的一种无毒、对环境友好的生物催化剂，是具有极强催化效率的特殊蛋白质，主要有淀粉酶、蛋白酶、果胶酶和过氧化氢酶等。', '生物酶在棉印染企业里主要应用于退浆、精炼 、过氧化氢漂白和后整理中，具有极强催化效率且环境友好。', '生物多样性保护', '生物酶催化/环境友好', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE009', '中国', '纺织', '冷轧堆前处理技术', '生产过程', '将传统的退浆、煮练、漂白三部前处理合为二步或一步工艺，其质量达到3道工序的水平。使用该工艺后平均生产用水量为24L/h，消耗蒸汽量为2.25t/h，比传统工艺用水量节省22.6t/h，蒸汽消耗量减少1.52 t/h。', '节约大量的能源和劳动力，减少废水的排放，比传统工艺节约用汽80%，节水75%，节约化工原料20%。', '能源节约及清洁化/水污染防治', '冷轧堆/节能降耗', '浙江美欣达印染集团股份有限公司、上海东美化工有限公司、北京第三印染厂、江苏悦达纺织集团有限公司、江苏常熟市精诚化工有限公司', '月产100万米各色机织物，采用ECONTROL染色机。投资金额为100万元。建设期为0.5年。年节约水资源费33万元，年节约蒸汽费145万元，年节约排污费16万元，年降低生产成本218万元，项目投资回收期为6年。', null, null, '纺织印染行业清洁生产技术调研报告、印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE010', '中国', '纺织', '活性染料冷轧堆染色技术', '生产过程', '主要是对染料和纤维的吸附、扩散和固色反应是在堆置中完成的，通常都在室温下进行的，较轧染省去了中间烘燥和汽蒸或焙烘两道工序，较大的节约了能耗，同时由于该工艺中染料水解少，固色率较高，在冷染后的皂洗过程中，也将节省大量的生产用水和蒸汽。该技术工艺流程短、设备简单、成本低，特别适合小批量、多品种生产，是印染企业提高产品档次、降低成本、改善环境的重要途径之一。', '染色得色率高、能耗低、准备周期短、重现性好、污染少，能节省大量的生产用水和蒸汽。与连续轧染工艺相比，冷轧堆染 色工艺在蒸汽与电的消耗方面降低30%~40%，染料固着率可提高20%~25%， 污水处理降耗10%。除此之外，冷轧堆染色工艺的条件相对连续轧染工艺也较温和，织物在染色过程中受到较小的张力，所以织物的染色效果和手感都有较大的提高。', '能源节约及清洁化/水污染防治', '冷轧堆/节能降耗/降低成本', '浙江美欣达印染集团股份有限公司', null, null, '由于冷轧堆染色是在室温下长时间缓慢固色，避免了泳移现象的发生，对于松紧厚薄织物，染料的渗透性都极佳，产品的色光更纯正;冷轧堆染色在室温下进行，没有烘燥和汽蒸工序，节省能源;冷轧堆染色具有较高的重现性和可靠性;冷轧堆染色设备投资少，占地面积小;冷轧堆染色采用开幅处理方式进行，织物没有折痕;冷轧堆染色处理过程中，织物受到的张力、摩擦力小，避免了织物的起毛问题，织物的表观效果和手感均有较大提高。', '纺织印染行业清洁生产技术调研报告、印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE011', '中国', '纺织', '涂料染色技术', '生产过程', '涂料染色是一种新型的染色技术，它用涂料代替染料依靠螯合剂及交联剂作用固着在织物上。不同于染料，涂料是不溶性的有色物质，由颜料、分散剂和润湿剂等组成，不能进入纤维内部，与纤维没有亲和力，不能按常规染料的染色工艺条件进行染色，只能借助黏合剂、交联剂的作用固着在纤维表面，即涂料染色是表面固着的过程。涂料染色是将涂料、黏合剂、其他助剂等混合制成色浆，根据产品质量、生产量等因素合理选择染色加工方式，浸渍或浸轧，织物均匀带液后，经预烘、焙烘，使之固着在织物表面。', '涂料染色与传统染色工艺相比加工工艺简单、加工流程短、色谱广、易于拼色、设备简单、成本低、生产效率高、节能节水、污水排放少。', '能源节约及清洁化', '涂料染色/简化工艺/环境友好', null, null, null, '涂料染色和染料染色相比，具有如下创新:1.涂料对任何纤维都没有亲和力，不存在上染过程，只存在黏着或着色过程。因此，它对各种纤维不存在选择性，适用于各种纤维，包括染料无法染色的玻璃纤维、金属纤维等，且特别适用于多组分纤维纺织品染色。2. 由于没有亲和力，使它在拼色时不存在竞染和配伍性问题，易于拼色，重现性好，便于从小样放大样和颜色控制。3.可以选用不同发色体系的有色物质，染色包括同时选用有机和无机有色物质拼用，不但色谱齐全，且可以获得染料染色无法得到的颜色或效果，如金银色、珠光和闪光色等特殊染色效果;也容易得到高耐光、耐气候和耐化学品作用的品种。4.加工工艺简单，因为只对纺织品发生着色或黏着过程。最简单的工艺只包括染色和烘干两步，加工流程短，设备简单，不仅生产效率高，更重要的是节能节水，污水排放少，因此大大降低了成本。', '纺织印染行业清洁生产技术调研报告、印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE012', '中国', '纺织', '喷墨印花技术', '生产过程', '喷墨印花技术是一种全新的印花方式 ，它摒弃了传统印花需要制版的复杂环节，直接在织物上喷印，提高了印花的精度，实现了小批量、多品种、多花色印花，而且解决了传统印花占地面积大、污染严重等问题，因此具有广阔的发展前景。', '较传统印花技术占地面积小、污染少。', '水污染防治', '喷墨印花/环境友好', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE013', '中国', '纺织', '高效水洗技术', '生产过程', '在印染加工中，水洗工序产生大量的废水，所以应用高效水洗设备和设计合理的水洗工艺，在生产中积极推行采用低水位、逆流水洗技术和设备，应用固色剂或交联剂提高固色效果，从而减轻水洗负担，降低用水量和洗液颜色深度。', '可以有效降低用水量和洗液颜色深度。', '水污染防治', '逆流水洗/节能减排', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE014', '中国', '纺织', '气流染色技术', '生产过程', '染液以雾状喷向织物，使得染液与织物在很短的时间内充分接触，以达到匀染的目的，其最大特点是浴比低 (浴比可至1：3)。', '气流染色机的特殊结构及运行方式弥你补了溢流染色机的诸多不足，具备了溢流染色机无法比拟的技术、经济优势。统计数据表明:使用气流染色机后，缩短染色加工时间20%，减少耗水量45%，减少耗蒸汽量30%，减少耗助剂量30%，减少排污量40%，提高了净洗效率和匀染效果，提高了产品质量。', '能源节约及清洁化', '节能减排/间歇式绳状染色', '金纺集团公司、邵阳纺织机械有限责任公司', null, null, '气流染色技术相比传统染色技术具有水消耗低、高效节能、节约染化料、减少污染、节省时间以及一次成功率高等优点，同时为织物间歇式绳状染色提供了新的加工方法，极大地促进了印染行业向高效、节能和环保的方向发展。', '纺织印染行业清洁生产技术调研报告、印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE015', '中国', '纺织', '变频调速技术', '生产过程', '变频调速可根据企业生产实际情况，调节电机交流电频率，从而调节电机转速，达到降低用电的目的。在棉印染企业中的染色机、烘干机、定型机 、罗茨风机、空压机、水泵等均可应用，投资回收期短，节电效果明显。', '有较好的节电效果', '资源高效利用', '变频调速/高效节电', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE016', '中国', '纺织', '工业锅炉节能改造', '生产过程', '常见工业锅炉节能改造主要有锅炉烟气余热利用和安装分层燃烧装置。锅炉烟气余热利用主要有安装余热蒸汽发生器或安装空气预热器，降低排烟温度。企业锅炉安装了分层燃烧装置，通过对燃煤进行粒度分选，使落到炉排上的燃煤按粒度大小分层排列，即大块煤在下面，中块煤在中间，细煤在煤层表面，有效避免炉排上出现的火口和燃烧不均匀现象。', '改善煤的着火条件，提高火床的热强度和燃烧速度，有利于煤的充分燃烧，提高锅炉热效率。', '资源高效利用', '锅炉烟气/余热利用/分层燃烧', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE017', '中国', '纺织', '印染废水余热利用', '生产过程', '印染企业生产过程中需消耗大量热能，同时排出大量高温废水，废水平均温度达到60~80 ℃，废水仍具有大量的潜热，通过热交换可回收利用废水余热。印染废水余热利用有两种方式：单体式和组合式。单体式主要用于连续排水的单台设备，如退煮漂联合机等。组合式可用于车间污水集中热回收回用。', '印染废水余热利用可明显节约蒸汽、电消耗，经济效益比较显著，尤其当排放的废水温度较高时。', '资源高效利用/能源节约及清洁化', '节能降耗/余热利用/高温废水', null, null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE018', '中国', '纺织', '集中退浆和连续式平幅退浆', '生产过程', '将退浆工序从染色机中分离出来，对残液连续回用。', '可以减少退浆残液COD排放量的60%，节水的同时减少液碱消耗。', '资源高效利用/水污染防治', '退浆残液/液碱回用', '江苏盛虹印染有限公司、江苏吴江二练亚氏印染有限责任公司', null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE019', '中国', '纺织', '短流程湿蒸染色技术', '生产过程', '该工艺是利用安装在反应蒸箱内人口处的远红外辐射器，将经过染色液浸渍轧吸的湿织物预热，使织物迅速升温，经过反应箱内少量蒸汽和干热空气的混合气体继续受热，2~3min固色，活性染料即固着于纤维，它的给色得以提高，并具有充分渗透、色泽均匀的优点。除一般织物外，此工艺特别适用于具有转移性的织物，如丝绒、灯芯绒、毛圈类织物。', '高得色率使染料用量降低10%-15%，高固色率和较低的水解作用使其在水洗过程中的用水量比传统工艺减少20%，年节省用水16000 t。', '资源高效利用', '湿蒸染色/高得色率/高效节水', '浙江美欣达印染集团股份有限公司', null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE020', '中国', '纺织', '高效节能前处理技术', '生产过程', '对生产用水、汽、液进行全自动控制，蒸洗箱采用逐格蛇行回流，均匀轧液、轧水，煮炼、漂白、丝光各段废水均利用换热器进行热回收利用', '与传统工艺生产中退煮漂、丝光工艺比较，该设备节水可达40%，每年可节水近14.1万t。', '资源高效利用', '余热利用/智能化/高效节水', '浙江美欣达印染集团股份有限公司', null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE021', '中国', '纺织', '无水液氨丝光技术', '生产过程', '无水液氨丝光工艺是在一个密闭的系统中进行的，从而防止了氨的泄漏。该工艺是用液氨作为丝光液，含有织物丝光、除氨、氨回收和循环、冷却系统和控制系统等。与烧碱相比较，氨更容易渗透到织物内部，与棉纤维作用，使织物在液氨作用下产生了轻微的膨胀，从而使织物表面产生绸缎的效果。织物在经受由磨损和多次水洗所带来的应力时，具有更好手感和柔软性。展现了更强的塑性和延展性，从而使服装的寿命和“新的”外观得以延长和保持。', '大大地减少了水的消耗和废水的产生。', '水污染防治', '液氨丝光/节水减排', '开平奔达纺织有限公司', null, null, null, '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE022', '中国', '纺织', '活性染料染色残液盐回用系统', '生产过程', '活性染料染色残液回用的基本原理是将棉染过程中含盐最高的染色残液单独收集，采用萃取分离技术将高盐高色度染色残液中的染料萃取分离出来，再将浓盐液回用于染色过程中。染色残液的处理过程中形成了水+盐（水相）和萃取剂（油相）两个体系的闭环循环，在萃取装置中相互作用，连续地将染色残浴中的水解染料提取出来，高盐浓液则循环使用。', '染色废水中的盐减排率大于70%，盐回用率可达80%以上，进入污水厂的污水含盐量显著降低，经常规生化处理就可达标排放。', '资源高效利用', '活性染料/盐液回用', null, '以棉染日产量20吨的染色车间为例，建立相应的染色残液盐回用系统的处理容量为100吨/日，处理染色残液的流量为5吨/小时。新建车间染色残液盐回用系统，需设备投资￥380万元（RMB），该盐回用系统的使用年限为10年。年运行维护费用:156万元（其中：每天处理染色残液100吨，需萃取费用2570元/天；按每班1人，三班倒需3人，再加领班1人，共需4人，人工费用为4×5000 / 30 = 667元/天，设备折旧按10年约1041元/天。）', '需要保持系统设备在10℃以上的运行温度，环境的其它因素无特别要求。整个系统自动化控制，自主运行，除最后的调盐过程外，无需人为操作干预。', '独创的活性染料染色残液盐回用系统和相应技术能够使生产企业用可承受的生产成本，解决棉染废水高盐排放对环境造成严重盐污染的难题，这是困扰印染行业几十年的世界难题。该技术的最大特点是在不改变原有染色生产要素，包括染料和设备设施，生产过程，工艺处方，和生产经验等，仅在车间增加一套盐回收系统，在源头上截留并针对小容量高盐度的染色残液进行有效处理，因此比其他任何一种印染废水除盐或无盐染色的方法都经济易行。', '纺织印染行业清洁生产技术调研报告', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE023', '中国', '纺织', '复合生物酶清洁印染前处理技术', '生产过程', '利用效率高、功能强、条件温和、环境友好的生物酶及复合生物酶在棉针织物印染过程中去除棉纤维上的杂质，或者完成其他重要加工过程的工艺。复合精炼酶wck-3主要用于棉纱和棉针织物染整加工中的精炼过程，复合除氧抛光酶wck-d主要用于练漂后去除织物上的残留H2O2，以利于后续加工和整理过程去除纤维表面的毛羽而实现织物表面光洁。', '在去除果胶物质的同时，又能与其他酶产生协同效应随即去除蜡状物和其他非纤维杂质，从而实现理想的精炼效果。', '生物多样性保护', '复合生物酶/染整精炼/环境友好', '江苏坤风纺织品有限公司', null, '实际产品设计定型，通过功能、性能测试；可进行产品小批量生产', '拥有独立自主知识产权、自主研发的复合生物酶wck-3和wck-d。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE024', '中国', '纺织', '无水染色技术', '生产过程', '无水染色是利用超临界CO2流体作为介质进行染色，该技术无污染、零排放，实现了染色过程的清洁化生产。', '可以彻底实现绿色、环保、清洁化生产。', '能源节约及清洁化', '超临界CO2流体/零排放', null, null, null, '开发SC-CO2流体染色专用商品化染料体系。与传统染色技术相比，SC-CO2流体染色技术中染料及其CO2介质的扩散性能好，扩散系数大，远高于传统水浴中的情况，可以节约能源和加工时间，而且染浴中一般不添加其他助剂，有利于染色产品的色泽鲜艳度和牢度的提高，以及保护纤维/织物的品质，同时无染色废水和其他废弃物产生。与水介质染色过程相比， SC-COz流体染色全过程无水，CO2无毒、不易燃烧、价格低廉，染料和COz可循环使用，“零排放”无污染，并具有上染速度快、上染率高的优势，可实现清洁、绿色、环保化加工。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE025', '中国', '纺织', '精炼酶一步一浴法前处理技术', '生产过程', '采用高效精练酶一步一浴法前处理工艺技术集精练、稳定、渗透、乳化、螯合、分散于一体，无需加入双氧水稳定剂、碱等，确保前处理的稳定性和一致性，适用于棉及其混纺织物的前处理过程。可以有效去除织物上的蜡类、浆料等疏水物质，使织物得到最佳的吸水性和白度。采用新工艺后，其工艺流程相当于传统工艺的1/2，无碱，综合能耗降低近50%，前处理污水排放COD值降低约50%，在生产综合成本降低的同时实现了节能减排。', '可以有效缩短前处理加工周期，克服了原工艺消耗高、效率低的缺点，既节约了污水处理费用又减轻了污水处理压力，工艺流程短，污染物排放少，综合成本低，实现节能环保、绿色加工。', '资源高效利用', '高效精炼酶/一步一浴法/', '锦州宏丰印染有限公司', null, null, '与传统工艺比较，用高效精练酶处理的织物手感较好，白度较高，克重损失小，断裂强力较大。毛细效应远远高于传统工艺。用高效精练酶前处理的织物染色和印花，其耐洗色牢度、耐摩擦色牢度及耐热压色牢度等指标高于传统的工艺。用高效精练酶技术处理所得废水的色度、pH值、悬浮物含量、COD、BOD 和总残渣量均低于传统工艺，这就确保了高效精练酶一步一浴法技术实现清洁生产的可能性。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE026', '中国', '纺织', '双氧水漂白前处理技术', '生产过程', '双氧水漂白简称氧漂。织物用双氧水漂白的工艺适用于各类纤维及其制品。氧漂后的织物白度和稳定性较好，能去除纤维素纤维中的大部分杂质和棉籽壳，适合练漂连续化生产。设备要求较高，生产成本比用次氯酸钠要高，但漂白效果较好，常用于较高档棉及含棉混纺织物的漂白。以“轧漂”和冷轧堆工艺为主，棉针织物可“浸漂”，可同时煮练。如织物上黏附铁、铜等重金属成分，则会催化双氧水的剧烈分解，使漂白织物产生破洞。双氧水对棉纺织物的漂白是在碱性介质中加热进行的，兼有一定的煮练作用，对煮练的要求较低。', '用双氧水漂白棉布具有许多优点，例如产品的白度较高并较为稳定，漂白的织物手感好，同时对退浆和煮练要求较低，便于漂练过程的连续化。此外，采用双氧水漂白无有害气体产生，可改善劳动条件。', '大气污染防治', null, null, null, null, null, '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE027', '中国', '纺织', '气液染色技术', '生产过程', '气液染色技术是改进传统溢流染色技术而得到的一种全新的间歇式绳状染色技术。气液染色机采用独立的气流循环和液流循环的设计理念，对加工织物产生不同作用，利于染料均匀上染织物。', '相较于传统的溢流染色工艺，气液染色工艺技术具有低浴比、高效率，能源、物料、时间消耗少的特点，一定程度上解决了染色废水及污染物高排放的问题。与传统气流雾化染色机相比，可节省用电60%以上，节省用水20%，节省蒸汽30%，且缩短工艺时间30%。解决了针织物幅宽变化、分散染料染深色浅、敏感色难控制，以及水洗不充分等问题。', '资源高效利用', '小浴比/节能减排', null, null, null, '减少气流循环无效损失，风机功率下降50%，染液与气流的循环采用了全新的设计结构，在充分提高气流有效利用率的同时，大幅度降低了风机消耗功率，将循环风机的额定功率降低到原来的 50%以下，解决了当前气流染色机电耗大的问题，在1:4以下低浴比染色时更加节水、省汽，特别是节电。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE028', '中国', '纺织', '小浴比匀流染色技术', '生产过程', '匀流染色机为超低浴比溢流染色机，不使用气流输送织物，整个绳状漂染处理过程包括前处理、染色及后续水洗工艺均由浴液完成。匀流染色机使溢流染色机能提供与气流染色机相同的小浴比，由于水洗效率更高，使得整个处理过程的总耗水量小于气流染色机。', '该技术的使用既达到了节能减排的目的，又成功避免了使用气流染色机对织物造成的张力大、布面起毛严重以及高耗电、高噪声和处理织物品种有限等实际生产问题。小浴比染色可减少能源和水的消耗，还可加快上染速率，在一定程度上提高固色率， 故可以减少盐用量，进行低盐染色，且能提高染料和碱剂的利用率，有利于改善善染色重现性、匀染性，提高净洗效率，确保产品品质。', '资源高效利用', '小浴比/节能减排', null, null, null, null, '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE029', '中国', '纺织', '泡沫染色技术', '生产过程', '泡沫染色技术是以空气代替部分水，使染液以泡沫的形式上染织物的一种节能节水的环保染色过程。泡沫染色以泡沫为传递介质，浴比小，可减少染料、助剂用量和用水量。', '以棉织物染色加工为例，相比传统浸轧方式，棉织物带液率下降 40%以上，可降低能耗 50%，节水40%，节约化学品20%，提高生产效率 50%，还能避免泳移、染色不匀。', '资源高效利用', '节能降耗/小浴比', null, null, null, null, '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE030', '中国', '纺织', '转移印花技术', '生产过程', '把颜料或染料印刷在纸、橡胶或其他载体上，然后移印到待印的商品上称转移印花，但狭义而言转移印花系指以纺织品作载体的移印技术。转移印花主要应用在聚酯纤维品上，随着转移印刷术的提高，在尼龙、丙烯腈、棉、麻、毛织品上也得到普遍应用。转移印花分湿法、干法、蒸汽法、真空法、热法等多种形式。转移印花法能够精确再现图案，便于机械化生产，且工艺简单，尤其是印花后处理工艺比传统印染简单而印花手感与印染相同，因而大有逐步替代老法印花之势。', '能够明显提高染料对棉纤维的转移率，且不需要对棉纤维进行预处理，不产生污水。', null, null, '苏州市彩旺纺织整理有限公司、无锡市东北塘宏良染色厂', null, null, null, '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE031', '中国', '纺织', '数码印花技术', '生产过程', '数码印花技术是随着计算机技术不断发展而逐渐形成的一种集机械、计算机电子信息技术为一体的高新技术产品。数码印花的生产过程通过各种数字化手段，例如将扫描、数字相片、图像或计算机制作处理的各种数字化图案输入计算机，再通过电脑分色印花系统处理后，由专用的RIP 软件通过对其喷印系统将各种专用染料(活性、分散、酸性为主的涂料)直接喷印到各种织物或其他介质上，再经过处理加工后在各种纺织面料上获得所需的高精度的印花产品。', '与传统印染工艺相比，数码印花的生产过程使原有的工艺路线大大缩短，接单速度快，打样成本大大降低。由于数码印花的生产工艺流程摆脱了传统印花在生产过程中分色描稿、制片、制网过程，从而大大缩短了生产时间。接受花样的方式，可以通过光盘、E-mail 等各种先进手段，一般打样时间不超过1个工作日，而传统打样的周期一般在1周左右。另外，由于工艺的简化，使打样成本也大大降低。', '资源高效利用', '数字化/', '上海八达纺织印染服装有限公司', null, null, '数码喷墨印花没有颜色限制、没有重复单元大小限制、制样时间显著缩短、几分钟内就能生成多色配色、筛网成本大大减少、企业生产效率大大提高、能够减少修补节约织物、减少库存和劳动力成本。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE032', '中国', '纺织', '泡沫整理技术', '生产过程', '泡沫整理是一种将化学品浆料(一般为水溶性)与表面活性剂共混，通过充入大量空气后，采用机械发泡的方式生成大量泡沫，并将泡沫施加于纺织品表面，提高纺织品附加值的一种纺织后整理方式。泡沫整理是将整理剂或涂层剂、发泡剂混合后发泡并施加到织物上，可单面处理也可双面处理，与常规轧烘焙整理相比，可大量节省化学品用量，还减少整理剂之间的相互影响，可用于各类功能面料的产品开发。', '由于泡沫是采用空气代替大部分的水，在节能减排上起到了很大作用，并且在很大程度上可以提高产品的品质。是一种环境友好型技术，在降低能耗、提高企业生产效率及产品质量方面具有很好的效果。', '资源高效利用', '泡沫整理/节能减排/环境友好', null, null, null, '泡沫整理技术系统可以控制工作液在被加工织物中的渗透距离，既可以使工作液只停留在织物表面，也可以使工作液浸透织物。因此可以对织物两面进行不同的加工，这是浸轧法根本无法实现的。并且与涂层整理相比，泡沫整理加工时工作液中通常只需要增添起泡剂和稳泡剂，与浸轧法的工作液并没有大的差别，所以施加到织物上，破泡后可以迅速渗透到纤维内部，因此加工后的织物耐洗性和手感都比较好。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE033', '中国', '纺织', '液氨整理技术', '生产过程', '液氨加工工艺是从纱线整理入手，以替代纱线丝光处理技术。由于液氨对纱线有着极强的渗透性，残留在纱线上的氨液比碱液更易于去除，而且比碱液丝光效果更好，因而引起重视。', '液氨整理可以提高纯棉等天然纤维及其混纺产品的穿着服用性能，其结合树脂整理新技术可进一步提高免烫效果。', null, null, null, null, null, null, '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE034', '中国', '纺织', '水性涂层整理技术', '生产过程', '涂层整理就是将一层或者多层能形成薄膜的高分子化合物均匀地涂覆在纺织物的表面，以实现织物的两面有不同功能的织物表面整理技术。涂层既能改变织物的外观风格，也能增加织物的功能性，使织物的附加值得到提高。然而大部分涂料都需要有机溶剂作为分散政介质给后续废水处理带来很大负荷。由于近年来有机溶剂价格高涨及环保部门对有机溶剂的使用和废物排放的严格限制，水性聚氨酯取代溶剂型聚氨酯成为一个重要的发展方向。', '可以在改变织物的外观风格，增加织物的功能性，使织物的附加值得到提高的同时，降低废水处理负荷。', '水污染防治', '水性涂料/涂层整理', null, null, null, '水性涂料是以水为分散介质代替有机溶剂，具有无污染、安全可靠、机械性能优良、相容性好、低挥发性有机化合物(VOCs)排放、易于改性等优点，符合环保、节能发展新理念，具有广阔的应用前景。', '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE035', '中国', '纺织', '交联盐析技术', '末端控制', '由于退浆废水中含有聚乙烯醇(PVA)、淀粉、羧甲基纤维素(CMC)和表面活性剂等难降解物质，COD浓度高、可生化性差，用单一的生化法难处理且耗时长，COD 去除效率一般不高。因此，退浆废水在处理前应尽量将 PVA 从废水中去除，盐析法操作简易，可去除废水中大部分 COD。若能从退浆废水中回收大部分 PVA，则在治理污染的同时可以变废为宝，既可以减少污染源又能获得一定的经济效益。', '盐析法处理棉退浆废水回收 PVA的效果很好，广泛应用于多个行业。在退浆废水中，除PVA 外还含有 CMC 及淀粉，故考虑可将废水中 PVA 回收用作黏合剂或其他领域中，具有较好的环境效益与经济效益。', '资源高效利用/水污染防治', '退浆废水/盐析法/PVA回收', null, null, null, null, '印染行业水污染治理成套集成技术', null);
INSERT INTO technology.Tech (NO, Country, Industry, CleanTechnology, ProductionStage, TechnologyOverview, Effect, Fields, Highlights, TypicalApplications, Cost, Stability, Innovation, Source, Comment) VALUES ('TE036', '中国', '纺织', '碱减量废水处理关键技术', '末端控制', '采用酸析法可回收碱减量废水中的对苯二甲酸，回收后的对苯二甲酸再经过提纯，可作为聚酯、电缆、除草剂等的生产原料。酸析法主要利用对苯二甲酸(TA)在酸性溶液中能沉淀析出的特点，在碱减量废水中加入酸，把pH值调至2~4，使TA析出结晶，实现与废水分离的一种方法。酸析法可实现废水中大部分对苯二甲酸的回收，使废水中的 COD 大幅度降低，同时操作简便，作为碱减量废水的预处理被广泛使用。', '碱减量废水经预处理回收对苯二甲酸后，不但实现了清洁生产，回收了可用资源，而且废水的 COD 值得到大幅度削减，使废水 COD 值从几万 mg/L 降到10000mg/L 以下，因此减轻了后续处理的负荷。', '资源高效利用/水污染防治', '碱减量废水/酸析法/TA回收', null, null, null, '碱减量废水的COD 值占印染废水总量的50%以上，而对苯二甲酸钠又占其中的75%， 所以碱减量废水经回收 TA后污水总 COD 值可以降低80%以上;更重要的是，混合印染废水在可生化性上发生了质的变化，使废水更加容易处理。从碱减量废水中回收TA给印染行业提供了有效治理污水的途径。', '印染行业水污染治理成套集成技术', null);
